mapper.update:
UPDATE advertisement
title = #{title}, content = #{content}, productName = #{productName}, imgUrl = #{imgUrl}, productUrl = #{productUrl}, updatedAt = NOW()
WHERE adsNum = #{adsNum}

mapper.select:
	SELECT a.*, s.storeName  FROM advertisement a
  JOIN sellerdetail s ON s.sellerNum=a.userNum





          s.storeName LIKE CONCAT('%', #{keyword}, '%')


          a.productName LIKE CONCAT('%', #{keyword}, '%')






      AND a.startDate >= #{startDateFrom}


      AND a.startDate <= #{startDateTo}




      AND a.userNum = #{userNum}


  ORDER BY a.createdAt DESC
  LIMIT #{recordSize} OFFSET #{offset}

SELECT count(*)  FROM advertisement a
  JOIN sellerdetail s ON s.sellerNum=a.userNum




          s.storeName LIKE CONCAT('%', #{keyword}, '%')


          a.productName LIKE CONCAT('%', #{keyword}, '%')





      AND a.startDate >= #{startDateFrom}


