mapper.update:
update advertisement set status=#{status}, updatedAt = NOW()
, startDate = NOW(), endDate = DATE_ADD(NOW(), INTERVAL 7 DAY)
where adsNum=#{adsNum}

UPDATE advertisement
title = #{title}, content = #{content}, productName = #{productName}, imgUrl = #{imgUrl}, productUrl = #{productUrl}, updatedAt = NOW()
WHERE adsNum = #{adsNum}

