mapper.update:
UPDATE alarm
SET isChecked = 1
WHERE alarmNum = #{alarmNum}

mapper.select:
		SELECT * FROM alarm a





	          a.type LIKE CONCAT('%', #{keyword}, '%')






		  AND Date(a.createdAt) >= #{startDateFrom}


		  AND Date(a.createdAt) <= #{startDateTo}





                    AND a.isChecked = 1  


                    AND a.isChecked = 0  





	      AND a.reNum = #{userNum}


	  ORDER BY a.createdAt DESC
	  LIMIT #{recordSize} OFFSET #{offset}

		SELECT count(*) FROM alarm a





	          a.type LIKE CONCAT('%', #{keyword}, '%')






		  AND Date(a.createdAt) >= #{startDateFrom}


		  AND Date(a.createdAt) <= #{startDateTo}





                    AND a.isChecked = 1  


                    AND a.isChecked = 0  





	      AND a.reNum = #{userNum}
