mapper.update:
update kockFarm set title=#{title}, quantity=#{quantity}, price=#{price}, shipDate=#{shipDate}, content=#{content}, imgUrl=#{imgUrl},
updatedAt= CURRENT_TIMESTAMP
where kockNum=#{kockNum}

mapper.select:
SELECT kc.*, u.userName, s.storeName FROM 
kockComment kc JOIN user u ON kc.userNum=u.userNum
JOIN sellerDetail s ON u.userNum=s.userNum
WHERE kockNum=#{kockNum}
ORDER BY createdAt;

mapper.update:
UPDATE kockfarm
SET isMatched = 1
WHERE kockNum = #{kockNum}

mapper.select:
select k.kockNum,k.userNum, k.cateNum, k.content, k.title, k.createdAt, k.isMatched, c.name AS name, count(kc.kcNum) AS kcCommentCount, m.sellerNum, s.storeName from kockfarm k join category c on k.catenum = c.catenum left join kockcomment kc on k.kockNum = kc.kockNum left join matching m on k.kockNum = m.kockNum left join sellerDetail s on m.sellerNum=s.sellerNum WHERE k.userNum=#{userNum} AND k.createdAt >= #{startDate}
AND k.isMatched = #{isMatched}
GROUP BY k.kockNum, k.userNum, k.cateNum, k.title,k.content, k.createdAt, k.isMatched, c.name, m.matchingNum, m.sellerNum, s.storeName ORDER BY k.createdAt DESC

SELECT * FROM(
SELECT kc.kcNum AS commentNum, kc.kockNum, kf.title, kc.content, kc.createdAt, '원댓글' AS commentType FROM kockComment kc JOIN kockfarm kf ON kc.kockNum = kf.kockNum WHERE kc.userNum = #{userNum}
UNION ALL
SELECT bc.reNum AS commentNum, kc.kockNum, kf.title, bc.content, bc.createdAt, '대댓글' AS commentType FROM babyComment bc JOIN kockComment kc ON bc.kcNum = kc.kcNum JOIN kockfarm kf ON kc.kockNum = kf.kockNum WHERE bc.userNum = #{userNum} ) AS allComments ORDER BY createdAt DESC LIMIT #{limit} OFFSET #{offset}
