mapper.select:

	select count(*)
	from packageOrder po
	Join package p on po.packageNum = p.packageNum
	Join sellerdetail s on p.sellerNum = s.sellerNum
	where po.userNum = #{userNum}

      AND po.orderDate >= #{startDate}


      AND po.orderDate <= #{endDate}


      AND po.deleveryStatus = #{deliveryStatus}

		select
		p.packageNum, p.sellerNum, p.packageName, p.imgUrl,
		s.sellerNum, s.storeName,
		po.userNum, po.pkTotalPrice, po.pkOrderNum ,po.orderedAt, po.deleveryStatus
	from packageOrder po
	Join package p on po.packageNum = p.packageNum
	Join sellerdetail s on p.sellerNum = s.sellerNum
	where po.userNum = #{userNum}

      AND po.orderedAt >= #{startDate}


      AND po.orderedAt <= #{endDate}


      AND po.deleveryStatus = #{deliveryStatus}

  ORDER BY po.orderedAt DESC

mapper.update:
update packageOrder
set deleveryStatus = #{deleveryStatus}
where pkOrderNum = #{pkOrderNum}

update packageOrder
set trackingNum =#{trackingNum}
where pkOrderNum=#{pkOrderNum}

mapper.select:
SELECT po.pkOrderNum, po.packageNum, p.packageName, po.pkTotalPrice, CONCAT( LEFT(u.userId, 1), REPEAT('*', CHAR_LENGTH(u.userId) - 2), RIGHT(u.userId, 1) ) as userId, ps.rname, ps.addr, ps.rphone, ps.deleveryDate, ps.subStartDate, ps.subRound, po.deleveryStatus, po.trackingNum, po.orderedAt FROM packageOrder po JOIN pakageSubscribe ps ON po.subNum = ps.subNum JOIN package p ON po.packageNum = p.packageNum JOIN user u ON po.userNum = u.userNum WHERE p.sellerNum = #{sellerNum}
AND
po.orderedAt BETWEEN #{startDate} AND #{endDate}
AND ps.deleveryDate = #{deliveryDay}
AND ps.rname LIKE CONCAT('%', #{searchKeyword}, '%')
AND po.pkOrderNum = #{searchKeyword}
AND po.packageNum = #{searchKeyword}
AND po.trackingNum = #{searchKeyword}
ORDER BY po.pkOrderNum DESC LIMIT #{offset}, #{pageSize}

    SELECT COUNT(*)
    FROM packageorder po
    JOIN pakagesubscribe ps ON po.subNum = ps.subNum
    JOIN package p ON po.packageNum = p.packageNum
    WHERE p.sellerNum = #{sellerNum}

    	AND 


	            po.orderedAt BETWEEN #{startDate} AND #{endDate}





	    AND ps.deleveryDate = #{deliveryDay}




	            AND ps.rname LIKE CONCAT('%', #{searchKeyword}, '%')


	            AND po.pkOrderNum = #{searchKeyword}


	            AND po.packageNum = #{searchKeyword}


	            AND po.trackingNum = #{searchKeyword}

SELECT po.pkOrderNum, p.packageName, CONCAT( LEFT(u.userId, 1), REPEAT('*', CHAR_LENGTH(u.userId) - 2), RIGHT(u.userId, 1) ) as userId, u.userName, s.storeName, seller.userId AS sellerId, ps.rname, ps.addr, ps.rphone, ps.deleveryDate, ps.subStartDate, ps.subEndDate, ps.subRound, po.deleveryStatus, po.trackingNum, po.orderedAt, po.pkTotalPrice FROM packageorder po JOIN pakagesubscribe ps ON po.subNum = ps.subNum JOIN package p ON po.packageNum = p.packageNum JOIN sellerdetail s ON p.sellerNum = s.sellerNum JOIN user seller ON s.userNum = seller.userNum JOIN user u ON po.userNum = u.userNum
AND DATE(po.orderedAt) BETWEEN #{startDate} AND #{endDate}
AND u.userId LIKE CONCAT('%', #{searchKeyword}, '%')
AND p.packageName LIKE CONCAT('%', #{searchKeyword}, '%')
AND po.pkOrderNum = #{searchKeyword}
ORDER BY po.orderedAt DESC LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM packageorder po JOIN pakagesubscribe ps ON po.subNum = ps.subNum JOIN package p ON po.packageNum = p.packageNum JOIN sellerdetail s ON p.sellerNum = s.sellerNum JOIN user seller ON s.userNum = seller.userNum JOIN user u ON po.userNum = u.userNum WHERE 1=1
AND DATE(po.orderedAt) BETWEEN #{startDate} AND #{endDate}
AND u.userId LIKE CONCAT('%', #{searchKeyword}, '%')
AND s.storeName LIKE CONCAT('%', #{searchKeyword}, '%')
AND po.trackingNum LIKE CONCAT('%', #{searchKeyword}, '%')
AND po.pkOrderNum = #{searchKeyword}
