mapper.select:
select p.productNum, p.sellerNum, p.productName,p.status, p.price, p.imgUrl, p.createdAt, p.salesVolume, c.name as cateName, COALESCE(pr.avgRating, 0) AS avgRating, COALESCE(pr.reviewCount, 0) as reviewCount, po.option,po.price,po.stock,po.createdAt,po.updatedAt
from product p join category c on p.cateNum = c.cateNum left join ( select productNum,AVG(pdRating) as avgRating, COUNT(*) as reviewCount from prodReview group by productNum )pr on p.productNum = pr.productNum left join productOption po on p.productNum = po.productNum where p.sellerNum = #{sellerNum}
and p.status = true
and p.status = false
order by p.price asc order by p.price desc order by p.salesVolume desc order by pr.reviewCount desc order by pr.avgRating desc order by p.createdAt desc
limit #{offset}, #{pageSize}

mapper.update:
	UPDATE product
   SET status = CASE productNum

           WHEN #{item.productNum} THEN #{item.status}

   END
   WHERE productNum IN

       #{item.productNum}

mapper.select:
select count(*) from product where sellerNum=#{sellerNum}
and status = true
and status = false
