mapper.update:
update productOrder
set deleveryStatus = #{deleveryStatus}
where pdOrderNum = #{pdOrderNum}

mapper.select:
SELECT po.pdOrderNum, poi.orderItem, poi.productNum, pOpt.option, pOpt.price AS optionPrice, poi.amount, (pOpt.price * poi.amount) AS totalPrice, po.orderDate, CONCAT( LEFT(u.userId, 1), REPEAT('*', CHAR_LENGTH(u.userId) - 2), RIGHT(u.userId, 1) ) as userId, u.userName, po.address, u.phone, po.orderStatus, po.deleveryStatus FROM productorder po JOIN productorderitem poi ON po.pdOrderNum = poi.pdOrderNum JOIN productoption pOpt ON poi.optionNum = pOpt.optionNum JOIN product p ON poi.productNum = p.productNum JOIN user u ON po.userNum = u.userNum WHERE p.sellerNum = #{sellerNum}
AND DATE(po.orderDate) BETWEEN #{startDate} AND #{endDate}
AND DATE(po.orderedAt) BETWEEN #{startDate} AND #{endDate}
AND u.userName LIKE CONCAT('%', #{searchKeyword}, '%')
AND po.pdOrderNum = #{searchKeyword}
AND poi.productNum = #{searchKeyword}
AND po.trackingNum = #{searchKeyword}
ORDER BY po.orderDate DESC LIMIT #{offset}, #{pageSize}

    SELECT 
        COUNT(DISTINCT po.pdOrderNum)
    FROM 
        productorder po
    JOIN 
        productorderitem poi ON po.pdOrderNum = poi.pdOrderNum
    JOIN 
    	productoption pOpt ON poi.optionNum = pOpt.optionNum
    JOIN 
        product p ON poi.productNum = p.productNum

    JOIN 
        user u ON po.userNum = u.userNum
    WHERE 
        p.sellerNum = #{sellerNum}




	        AND DATE(po.orderDate) BETWEEN #{startDate} AND #{endDate}


	        AND DATE(po.orderedAt) BETWEEN #{startDate} AND #{endDate}










                AND u.userName LIKE CONCAT('%', #{searchKeyword}, '%')


                AND po.pdOrderNum = #{searchKeyword}


                AND poi.productNum = #{searchKeyword}


                AND po.trackingNum = #{searchKeyword}
