mapper.select:
SELECT * FROM user u
JOIN sellerdetail s ON u.userNum=s.userNum
WHERE s.userNum = #{userNum}

SELECT u.*, sd.* FROM sellerdetail sd JOIN user u ON sd.userNum = u.userNum WHERE sd.isAlarm = 1 AND u.isSeller = 1 AND u.isDeleted = 0
AND sd.storeName LIKE CONCAT('%', #{keyword}, '%')
ORDER BY a.createdAt DESC LIMIT #{recordSize} OFFSET #{offset}
